//     height: 100%;
//     width: 100%;
//     background: url(${HeroImage}) no-repeat center center;
//     top: 0;
//     background-size: cover;
//     z-index: -1;
// `

// const Text = styled.div`
//     font-family: 'Space Grotesk', sans-serif;
//     font-size: 8rem;
//     color: #999;
//     mix-blend-mode: difference;
//     font-weight: 600;
//     position: fixed;
